import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/moduels/search/search.dart';import 'package:news_app/shared/cubit/cubit.dart';import 'package:news_app/shared/cubit/state.dart';import 'package:news_app/shared/network/remote/dio.dart';class NewsLayout extends StatelessWidget {  const NewsLayout({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocConsumer<NewsCubit, NewsState>(      listener: (context, state) {},      builder: (context, state) {        var cubit = NewsCubit.get(context);        return Scaffold(          appBar: AppBar(            actions: [              IconButton(                  onPressed: () {                    Navigator.push(                      context,                      MaterialPageRoute(                        builder: (context) => Search(),                      ),                    );                  },                  icon: Icon(                    Icons.search_rounded,                  )),              SizedBox(                width: 20.0,              ),              IconButton(                onPressed: () {                  NewsCubit.get(context).changeTheme();                },                icon: Icon(                  Icons.brightness_4_outlined,                ),              ),              SizedBox(                width: 10.0,              ),            ],            title: Text(              'News App',            ),          ),          bottomNavigationBar: BottomNavigationBar(            currentIndex: cubit.currentIndex,            items: cubit.items,            onTap: (int index) {              cubit.bottomNavBar(index);            },          ),          body: cubit.screens[cubit.currentIndex],        );      },    );  }}// POST// UPDATE// DELETE// GET// base url : https://newsapi.org/// method (url) : v2/top-headlines?// queries : country=eg&category=business&apiKey=65f7f556ec76449fa7dc7c0069f040ca