import 'package:bloc/bloc.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/shared/componants/componants.dart';import 'package:news_app/shared/cubit/cubit.dart';import 'package:news_app/shared/cubit/state.dart';class Search extends StatelessWidget {  var searchController = TextEditingController();  @override  Widget build(BuildContext context) {    return BlocConsumer<NewsCubit, NewsState>(      listener: (context, state) {},      builder: (context, state) {        var list = NewsCubit.get(context).search;        return Scaffold(          appBar: AppBar(),          body: Padding(            padding: const EdgeInsets.all(20.0),            child: Column(              children: [                TextFormField(                  controller: searchController,                  keyboardType: TextInputType.text,                  validator: (value) {                    if (value!.isEmpty) {                      return 'search must not be empty';                    }                    return null;                  },                  onTap: () {                    NewsCubit.get(context).getSearch(searchController.text);                    print(searchController.text);                  },                  onChanged: (value) {                    NewsCubit.get(context).getSearch(value);                    print(value);                  },                  decoration: InputDecoration(                    border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(                        30.0,                      ),                    ),                    labelText: 'search',                    prefixIcon: Icon(                      Icons.search,                    ),                  ),                ),                Expanded(                  child: newsScreen(list),                ),              ],            ),          ),        );      },    );  }}