import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/shared/componants/componants.dart';import 'package:news_app/shared/cubit/cubit.dart';import 'package:news_app/shared/cubit/state.dart';class SportScreen extends StatelessWidget {  @override  Widget build(BuildContext context) {    return BlocConsumer<NewsCubit, NewsState>(      listener: (context, state) {},      builder: (context, state) {        var list = NewsCubit.get(context).sports;        return list.length > 0            ? ListView.separated(                physics: BouncingScrollPhysics(),                separatorBuilder: (context, index) => Padding(                  padding: const EdgeInsetsDirectional.only(                    start: 15.0,                  ),                  child: Container(                    height: 1,                    color: Colors.grey[300],                  ),                ),                itemBuilder: (context, index) => newsCard(list[index], context),                itemCount: list.length,              )            : Center(child: CircularProgressIndicator());      },    );  }}